mapper.select:
select 

from cart
where user_id = #{userId,jdbcType=INTEGER}
  and sale_info_id = #{saleInfoId,jdbcType=INTEGER}

mapper.delete:
delete from cart
where user_id = #{userId,jdbcType=INTEGER}
  and sale_info_id = #{saleInfoId,jdbcType=INTEGER}

mapper.insert:
insert into cart (user_id, sale_info_id, quantity
  )
values (#{userId,jdbcType=INTEGER}, #{saleInfoId,jdbcType=INTEGER}, #{quantity,jdbcType=INTEGER}
  )

mapper.update:
update cart


    quantity = #{quantity,jdbcType=INTEGER},


where user_id = #{userId,jdbcType=INTEGER}
  and sale_info_id = #{saleInfoId,jdbcType=INTEGER}

update cart
set quantity = #{quantity,jdbcType=INTEGER}
where user_id = #{userId,jdbcType=INTEGER}
  and sale_info_id = #{saleInfoId,jdbcType=INTEGER}
